import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import Sidebar from '../components/Sidebar';
import { userAPI, notificationsAPI } from '../api';

const Settings = ({ user, onLogout }) => {
  const [activeTab, setActiveTab] = useState('profile');
  const [settings, setSettings] = useState({
    profile: { firstName: '', lastName: '', email: '', phone: '' },
    notifications: {
      email: { enabled: true, fraudAlerts: true, renewalReminders: true, spendingAlerts: true },
      sms: { enabled: false, fraudAlerts: true, renewalReminders: false },
      push: { enabled: true, fraudAlerts: true, renewalReminders: true }
    },
    preferences: { currency: 'INR', timezone: 'Asia/Kolkata', language: 'en' },
    security: { twoFactorAuth: false, loginNotifications: true, sessionTimeout: 30 }
  });

  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchProfile = async () => {
      setIsLoading(true);
      setError('');
      try {
        const profile = await userAPI.getProfile();
        const notif = await notificationsAPI.getSettings();
        setSettings(prev => ({
          ...prev,
          profile: {
            firstName: profile?.firstName || '',
            lastName: profile?.lastName || '',
            email: profile?.email || '',
            phone: profile?.phone || ''
          },
          notifications: notif || prev.notifications
        }));
      } catch (_) {
        setError('Failed to load profile');
      } finally {
        setIsLoading(false);
      }
    };
    fetchProfile();
  }, []);

  const handleInputChange = (section, field, value) => {
    setSettings(prev => ({
      ...prev,
      [section]: {
        ...prev[section],
        [field]: value
      }
    }));
  };

  const handleNestedInputChange = (section, subsection, field, value) => {
    setSettings(prev => ({
      ...prev,
      [section]: {
        ...prev[section],
        [subsection]: {
          ...prev[section][subsection],
          [field]: value
        }
      }
    }));
  };

  const handleSave = async (section) => {
    setIsLoading(true);
    setMessage('');
    setError('');
    
    try {
      if (section === 'Profile') {
        await userAPI.updateProfile(settings.profile);
      } else if (section === 'Notification') {
        await notificationsAPI.updateSettings(settings.notifications);
      }
      
      setMessage(`${section} settings saved successfully!`);
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      setError(`Error saving ${section} settings`);
    } finally {
      setIsLoading(false);
    }
  };

  const handleTestNotification = async (type) => {
    setIsLoading(true);
    try {
      await notificationsAPI.testNotification(type);
      setMessage(`Test ${type} notification sent!`);
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      setError(`Error sending test ${type} notification`);
    } finally {
      setIsLoading(false);
    }
  };

  const renderProfileTab = () => (
    <div style={{ maxWidth: '600px' }}>
      <h3 style={{ marginBottom: '1.5rem', color: '#333' }}>Profile Information</h3>
      
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem', marginBottom: '1rem' }}>
        <div>
          <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold', color: '#333' }}>
            First Name
          </label>
          <input
            type="text"
            value={settings.profile.firstName}
            onChange={(e) => handleInputChange('profile', 'firstName', e.target.value)}
            style={{
              width: '100%',
              padding: '0.75rem',
              border: '1px solid #ddd',
              borderRadius: '6px',
              fontSize: '1rem'
            }}
          />
        </div>
        <div>
          <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold', color: '#333' }}>
            Last Name
          </label>
          <input
            type="text"
            value={settings.profile.lastName}
            onChange={(e) => handleInputChange('profile', 'lastName', e.target.value)}
            style={{
              width: '100%',
              padding: '0.75rem',
              border: '1px solid #ddd',
              borderRadius: '6px',
              fontSize: '1rem'
            }}
          />
        </div>
      </div>

      <div style={{ marginBottom: '1rem' }}>
        <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold', color: '#333' }}>
          Email Address
        </label>
        <input
          type="email"
          value={settings.profile.email}
          onChange={(e) => handleInputChange('profile', 'email', e.target.value)}
          style={{
            width: '100%',
            padding: '0.75rem',
            border: '1px solid #ddd',
            borderRadius: '6px',
            fontSize: '1rem'
          }}
        />
      </div>

      <div style={{ marginBottom: '1.5rem' }}>
        <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold', color: '#333' }}>
          Phone Number
        </label>
        <input
          type="tel"
          value={settings.profile.phone}
          onChange={(e) => handleInputChange('profile', 'phone', e.target.value)}
          style={{
            width: '100%',
            padding: '0.75rem',
            border: '1px solid #ddd',
            borderRadius: '6px',
